<section data-content="request-new-ticket" class="content content--ticket">
    <h2>Support Center</h2>

    <div class="card card--noresize card--ticket">
        <h3>Request New Ticket</h3>
        <select class="card--ticket__categories" id="ticket-categories">
            <option value="">Select the category</option>
            <option value="Web">Web</option>
            <option value="Design">Design</option>
            <option value="Management">Management</option>
        </select>
    </div>

    <div class="card card--noresize card--ticket">

        <h3>Title</h3>
        <input type="text" class="card--ticket__title" placeholder="Request Title">

        <h3>Description</h3>
        <textarea class="card--ticket__content" placeholder="Describe your request..."></textarea>

        <button class="card--ticket__send">Send your request</button>
    </div>

</section>

